mapper.select:
select m.oid, d.gid, d.gname, d.gsize, d.gamount, d.gcolor, d.gprice, d.gimage, d.confirmed 
from orders o join orderproductdetail d on o.opindex = d.opindex 
where o.mid = #{mid}

select count(*)
from seller
where sid = #{sid}

select *
from seller
where sid = #{sid} and spw = #{spw}
